import React from 'react';
import Data from './Data.json'

class HornedBeast extends React.Component {

constructor (props){
  super(props);
  this.state= {
    likes:0
   
  };
};

handleLikes=() => {
  this.setState({
    likes:this.state.likes + 1
  })
}








  render() {
    return (
      <article>
        <h2>{this.props.title}</h2>
        
        <p>{this.state.likes} Likes</p>

        <p onClick={this.handleLikes}> Click here to like this picture</p>
        <img src={this.props.image_url} alt={this.props._id} title={this.props.title}/>
        
      </article>
    )
  }
}

export default HornedBeast;
